import styled from 'styled-components';

const primaryBlue = '#03497E';

const Button = styled.button`
  padding: 8px 16px;
  font-size: 1rem;
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
`;

export const PrimaryButton = styled(Button)`
  background-color: ${primaryBlue};
  border: none;
  color: white;
  `;
