import styled from 'styled-components';
import { mgaTheme, typeScale, serifFont, sansSerifFont } from '../utils';


//Primary Buttons
const buttonPrimaryBorder = 'none';


//Styled Button Components

const Button = styled.button`
  padding: 16px 16px;
  margin: 8px;
  font-family: ${sansSerifFont};
  font-size: ${typeScale.header5};
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  transition: background-color 0.2s linear, color 0.2s linear;
`;

export const PrimaryButton = styled(Button)`
  color: ${mgaTheme.neutralColorlWhite};
  background-color: ${mgaTheme.primaryColorDark};
  border: ${buttonPrimaryBorder};  

  &:hover {
    background-color: ${mgaTheme.primaryColorMedium};
  }

  &:focus {
    background-color: ${mgaTheme.primaryColorMedium};
    outline: 3px solid ${mgaTheme.primaryColorDark};
    outline-offset: 2px;
  }

  &:active {
    background-color: ${mgaTheme.primaryColorMedium};
    outline: none;
    outline-offset: none;
  }

  &:disabled {
    cursor: not-allowed;
    background-color: ${mgaTheme.primaryColorDisabled};
  }

`;
