import styled from 'styled-components';
import { myTheme, typeScale, serifFont, sansSerifFont } from '../utils';


//Primary Buttons
const buttonPrimaryBorder = 'none';

//Secondary Buttons
const buttonSecondaryDefaultBorder = '1px solid ' &&  `${myTheme.primaryColor}`;
const buttonSecondaryHoverBorder = 'none';
const buttonSecondaryFocusBorder = 'none';
const buttonSecondaryActiveBorder = 'none';
const buttonSecondaryDisabledBorder = '1px solid ' && `${myTheme.primaryDisabledColor}`;

//Tertiary Buttons
const buttonTertiaryBorder = 'none';


//Styled Button Components

const Button = styled.button`
  padding: 16px 16px;
  margin: 8px;
  font-family: ${sansSerifFont};
  font-size: ${typeScale.paragraph};
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  border: none;
`;

export const PrimaryButton = styled(Button)`
  color: ${myTheme.textColorOnPrimary};
  background-color: ${myTheme.primaryColor};
  border: ${buttonPrimaryBorder};
`;

export const SecondaryButton = styled(Button)`
  color: ${myTheme.buttonColorInverted};
  background-color: ${myTheme.neutral};
  border: ${buttonSecondaryDefaultBorder};
`;
