import styled from 'styled-components';
import { myTheme, typeScale, serifFont, sansSerifFont } from '../utils';


//Primary Buttons
const buttonPrimaryBorder = 'none';


//Styled Button Components

const Button = styled.button`
  padding: 16px 16px;
  margin: 8px;
  font-family: ${sansSerifFont};
  font-size: ${typeScale.header5};
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  border: none;
  transition: background-color 0.2s linear, color 0.2s linear;

  &:hover {
    background-color: ${myTheme.primaryHoverColor};
  }
`;

export const PrimaryButton = styled(Button)`
  color: ${myTheme.buttonColorOnPrimary};
  background-color: ${myTheme.buttonBackgroundPrimary};
  border: ${buttonPrimaryBorder};
`;
