import React from "react";
import styled from "styled-components";
import { mgaTheme, typeScale, serifFont, sansSerifFont } from "../utils";
import { PrimaryButton } from "./buttons";
import { Illustrations } from "../assets";
import { CloseIcon } from "../assets/icons/CloseIcon";


const ModalWrapper = styled.div`
    width: 800px;
    height: 600px;
    box-shadow: 0 4px 16px rgba(0,0,0,0.2);
    background-color: neutralColorWhite;
    color: ${mgaTheme.textColorPrimary};
    display: flex;
    flex-direction: column;
    justify-content: center;
    align-items: center;
    position: relative;
    margin: 2.0rem auto;
    `;

const SignUpHeader = styled.div`
    margin: 1.5rem 0 0.5rem 0;
    font-size: ${typeScale.header3};
    font-family: ${sansSerifFont};
    `;

const SignUpText = styled.div`
    font-size: ${typeScale.paragraph};
    font-family: ${serifFont};
    maxWidth: 70%;
    text-align: center;
    `;

const CloseModalButton = styled.button`
    cursor: pointer;
    background: none;
    border: none;
    position: absolute;
    right: 40px;
    top: 40px;
    width: 24px;
    height: 24px;
    padding: 0;
    margin: 32px 0 16px 0;
    `;

export const SignUpModal = () => {

/*  Create a 'click' listener for the CloseModalButton with id="close-modal-button".
    When the button is clicked write "SignUpModal Close Modal" to the console.
    document.getElementById("close-modal-button").addEventLister("click", () => {
        console.log("SignUpModal Close Modal");
    });
*/

    return (
        <ModalWrapper>
            <CloseModalButton id="close-modal-button" aria-label="Close modal">
                <CloseIcon />
            </CloseModalButton>
            <img 
                src={Illustrations.SignUp}
                alt="Sign up for an account"
                aria-hidden="true"
                width={"400px"}
            />
            <SignUpHeader>Sign Up Today!</SignUpHeader>
            <SignUpText>Sign up today to get access to all of our content and features!</SignUpText>    
            <div style={{marginTop: "2rem"}}>
                <PrimaryButton onClick={()=>console.log("Signed Up!!")}>Sign Up!</PrimaryButton>
            </div>
        </ModalWrapper>
    )
}

export const LoginModal = () => {
    return (
        <ModalWrapper>
            <CloseModalButton aria-label="Close modal">
                <CloseIcon />
            </CloseModalButton>
        </ModalWrapper>
    )
}
