import styled from 'styled-components';

//Primary Buttons
const buttonPrimaryDefaultBackground = '#03497E';
const buttonPrimaryHoverBackground = '#0596D5';
const buttonPrimaryFocusBackground = '#0596D5';
const buttonPrimaryActiveBackground = '#9DEBFC';
const buttonPrimaryDisabledBackground = '#999999';

const buttonPrimaryColor = '#FFFFFF';
const buttonPrimaryActiveColor = '#9DEBFC';

const buttonPrimaryBorder = 'none';

//Secondary Buttons

const buttonSecondaryDefaultBackground = '#FFFFFF';
const buttonSecondaryHoverBackground = '#0596D5';
const buttonSecondaryFocusBackground = '#0596D5';
const buttonSecondaryActiveBackground = '#9DEBFC';
const buttonSecondaryDisabledBackground = '#FFFFFF';

const buttonSecondaryDefaultColor = '#03497E';
const buttonSecondaryHoverColor = '#FFFFFF';
const buttonSecondaryFocusColor = '#FFFFFF';
const buttonSecondaryActiveColor = '#03497E';
const buttonSecondaryDisabledColor = '#999999';

const buttonSecondaryDefaultBorder = '1px solid #03497E';
const buttonSecondaryHoverBorder = 'none';
const buttonSecondaryFocusBorder = 'none';
const buttonSecondaryActiveBorder = 'none';
const buttonSecondaryDisabledBorder = '1px solid #999999';

//Tertiary Buttons
const buttonTertiaryDefaultBackground = '#FFFFFF';
const buttonTertiaryHoverBackground = '#FFFFFF';
const buttonTertiaryDisabledBackground = '#FFFFFF';

const buttonTertiaryDefaultColor = '#03497E';
const buttonTertiaryHoverColor = '#03497E';
const buttonTertiaryDisabledColor = '#999999';

const buttonTertiaryDefaultBorder = 'none';
const buttonTertiaryHoverBorder = 'none';
const buttonTertiaryDisabledBorder = '1px solid #999999';

//Styled Button Components

const Button = styled.button`
  padding: 8px 16px;
  margin: 8px;
  font-size: 1rem;
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
`;

export const PrimaryButton = styled(Button)`
  color: ${buttonPrimaryColor};
  background-color: ${buttonPrimaryDefaultBackground};
  border: ${buttonPrimaryBorder};
`;

export const SecondaryButton = styled(Button)`
  color: ${buttonSecondaryDefaultColor};
  background-color: ${buttonSecondaryDefaultBackground};
  border: ${buttonSecondaryDefaultBorder};
`;

export const TertiaryButton = styled(Button)`
  color: ${buttonTertiaryDefaultColor};
  background-color: ${buttonTertiaryDefaultBackground};
  border: ${buttonTertiaryDefaultBorder};
`;

export const DisabledButton = styled(Button)`
  color: ${buttonPrimaryColor};
  background-color: ${buttonPrimaryDisabledBackground};
  border: ${buttonPrimaryBorder};
  `;
