import styled from 'styled-components';
import { primary, neutral, success, error, warning } from '../utils/colors';


//Primary Buttons
const buttonPrimaryDefaultBackground = `${primary[100]}`;
const buttonPrimaryHoverBackground = primary[200];
const buttonPrimaryFocusBackground = primary[200];
const buttonPrimaryActiveBackground = primary[300];
const buttonPrimaryDisabledBackground = primary[400];

const buttonPrimaryColor = neutral[100];
const buttonPrimaryActiveColor = primary[100];

const buttonPrimaryBorder = 'none';

//Secondary Buttons

const buttonSecondaryDefaultBackground = neutral[100];
const buttonSecondaryHoverBackground = primary[200];
const buttonSecondaryFocusBackground = primary[200];
const buttonSecondaryActiveBackground = primary[300];
const buttonSecondaryDisabledBackground = primary[400];

const buttonSecondaryDefaultColor = primary[100];
const buttonSecondaryHoverColor = neutral[100];
const buttonSecondaryFocusColor = neutral[100];
const buttonSecondaryActiveColor = primary[100];
const buttonSecondaryDisabledColor = primary[400];

const buttonSecondaryDefaultBorder = '1px solid ' &&  primary[100];
const buttonSecondaryHoverBorder = 'none';
const buttonSecondaryFocusBorder = 'none';
const buttonSecondaryActiveBorder = 'none';
const buttonSecondaryDisabledBorder = '1px solid ' && primary[400];

//Tertiary Buttons
const buttonTertiaryDefaultBackground = neutral[100];
const buttonTertiaryHoverBackground = neutral[100];
const buttonTertiaryDisabledBackground = neutral[100];

const buttonTertiaryDefaultColor = primary[100];
const buttonTertiaryHoverColor = primary[100];
const buttonTertiaryDisabledColor = primary[400];

const buttonTertiaryDefaultBorder = 'none';
const buttonTertiaryHoverBorder = 'none';
const buttonTertiaryDisabledBorder = `1px solid ` + primary[400];

//Styled Button Components

const Button = styled.button`
  padding: 16px 16px;
  margin: 8px;
  font-size: 1rem;
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
  border: none;
`;

export const PrimaryDefaultButton = styled(Button)`
  color: ${buttonPrimaryColor};
  background-color: ${buttonPrimaryDefaultBackground};
  border: ${buttonPrimaryBorder};
`;

export const SecondaryDefaultButton = styled(Button)`
  color: ${buttonSecondaryDefaultColor};
  background-color: ${buttonSecondaryDefaultBackground};
  border: ${buttonSecondaryDefaultBorder};
`;

export const TertiaryDefaultButton = styled(Button)`
  color: ${buttonTertiaryDefaultColor};
  background-color: ${buttonTertiaryDefaultBackground};
  border: ${buttonTertiaryDefaultBorder};
`;

export const PrimaryDisabledButton = styled(Button)`
  color: ${buttonPrimaryColor};
  background-color: ${buttonPrimaryDisabledBackground};
  border: ${buttonPrimaryBorder};
  `;
