import styled from 'styled-components';

const primaryButtonBackground = '#03497E';
const secondaryButtonBackground = '#FFFFFF';
const tertiaryButtonBackground = '#FFFFFF';

const primaryButtonFontColor = '#FFFFFF';
const secondaryButtonFontColor = '#03497E';

const primaryButtonBorder = 'none';
const secondaryButtonBorderColor = '1px solid #03497E';

const Button = styled.button`
  padding: 8px 16px;
  margin: 8px;
  font-size: 1rem;
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
`;

export const PrimaryButton = styled(Button)`
  color: ${primaryButtonFontColor};
  background-color: ${primaryButtonBackground};
  border: ${primaryButtonBorder};
  `;

export const SecondaryButton = styled(Button)`
  color: ${secondaryButtonFontColor};
  background-color: ${secondaryButtonBackground};
  border: ${secondaryButtonBorderColor};
  `;
