import { primary, neutral, success, error, warning } from './colors';
import { sansSerifFont, serifFont } from './typography';

export const myTheme = {

    primaryColor: primary[100],
    primaryHoverColor: primary[200],
    primaryActiveColor: primary[300],
    primaryDisabledColor: primary[400],

    buttonColorOnPrimary: neutral[100],
    buttonColorInverted: primary[100],
    buttonColorDisabled: primary[400],

    buttonBackgroundPrimary: primary[100],
    buttonBackgroundNeutral: neutral[100],
    buttonBackgroundDisabled: primary[400],

    sansSerifFont, 
    serifFont,

};
