import styled from 'styled-components';

const Button = styled.button`
  padding: 8px 16px;
  font-size: 1rem;
  text-align: center;
  border-radius: 2px;
  min-width: 100px;
  cursor: pointer;
  font-family: 'Montserrat', sans-serif;
`;

const PrimaryButton = styled(Button)`
  background-color: red;
  border: none;
  color: white;
  `;

  export default PrimaryButton;
